import { Component, Input } from '@angular/core';
import { PokemonDetail } from '../service/models/pokemon.detail';

@Component({
  selector: 'app-pokemon-card',
  templateUrl: './pokemon-card.component.html',
  styleUrls: ['./pokemon-card.component.sass']
})
export class PokemonCardComponent {
  @Input()
  pokemon!: PokemonDetail;

  showModal(pokemon: PokemonDetail){
    console.log(pokemon.name)
  }
}


<div class="card" (click)="showModal(pokemon)">
    <img class="image" src="https://projectpokemon.org/images/normal-sprite/{{pokemon.name}}.gif" [alt]="pokemon.name">
    <p class="name">{{pokemon.name}}</p>
</div>


<div class="modal">
    <p></p>
</div>
