import { Component, Input } from '@angular/core';
import { PokemonList } from '../service/models/pokemon.list';
import { PokemonDetail } from '../service/models/pokemon.detail';
import { PokemonService } from '../service/PokemonService';

@Component({
  selector: 'app-pokemon-list',
  templateUrl: './pokemon-list.component.html',
  styleUrls: ['./pokemon-list.component.sass']
})
export class PokemonListComponent {

  pokemons: PokemonList[] = [];

  classicMode: boolean = true;

  private offset: number | undefined;
  isLastPage = false;

  searchPokemon: PokemonDetail = new PokemonDetail();
  isSearching = false;

  constructor(private pokemonService: PokemonService){
    this.getAllPokemons();
  }

  getAllPokemons(){
    this.pokemonService.getPokemonList(0).subscribe((list: PokemonList[]) => {
      if(list.length === 0) {
        this.isLastPage = true;
        this.pokemons = list;
        console.log(this.pokemons);
      }

      if(!this.isLastPage) {
        console.log(list)
      }
    });
  }

}
