import { Component, Input } from '@angular/core';
import { PokemonDetail } from '../service/models/pokemon.detail';

@Component({
  selector: 'app-pokemon-card',
  templateUrl: './pokemon-card.component.html',
  styleUrls: ['./pokemon-card.component.sass']
})
export class PokemonCardComponent {
  @Input()
  pokemon!: PokemonDetail;

  corDeFundoMapping: any = {
    "grass": "#228B22",
    "fire": "#F4A460"
    "psychic": psychic
    // Outros valores e cores
  };
  show: boolean = false ;

  showModal(){
    this.show = !this.show
  }
}


<div class="card" (click)="showModal()" [style.background-color]="corDeFundoMapping[pokemon.types[0].type.name]">
    <img class="image" src="https://projectpokemon.org/images/normal-sprite/{{pokemon.name}}.gif" [alt]="pokemon.name">
    <p class="name">{{pokemon.name}}</p>
</div>

<ng-container *ngIf="show">
    <div class="modal">
        <p>{{pokemon.name}}</p>
        <p>{{pokemon.types[0].type.name}}</p>
        <p>{{pokemon.types[1].type.name}}</p>
    </div>
    <div class="overlay" (click)="showModal()"></div>
</ng-container>
